/******/ (function() { // webpackBootstrap
/******/ 	"use strict";
var __webpack_exports__ = {};
// // Import vendor jQuery plugin example
// import '~/app/libs/mmenu/dist/mmenu.js'
document.addEventListener('DOMContentLoaded', function () {
  // Custom JS
  // showing items
  document.addEventListener('click', function (event) {
    if (event.target == document.querySelector('.email-confirmation')) {
      document.querySelector('.modal-window').classList.remove('hide');
    }

    if (event.target == document.querySelector('.modal-container') || event.target == document.querySelector('.modal-window') || event.target == document.querySelector('.close img') || event.target == document.querySelector('.btn-red')) {
      document.querySelector('.modal-window').classList.add('hide');
    }

    if (event.target == document.querySelector('.chenge-phone')) {
      document.querySelector('.phone-chenge-error-popup').classList.remove('hide');
    }

    document.querySelectorAll('.action-container .expand img').forEach(function (el) {
      if (event.target == el) {
        imgFullWidth(el);
      }
    });
  }); // hiding items

  document.addEventListener('click', function (event) {
    if (event.target == document.querySelector('.modal-container') || event.target == document.querySelector('.modal-window') || event.target == document.querySelector('.close img') || event.target == document.querySelector('.btn-red')) {
      document.querySelector('.modal-window').classList.add('hide');
    }

    if (event.target != document.querySelector('.chenge-phone')) {
      document.querySelector('.phone-chenge-error-popup').classList.add('hide');
    }

    if (event.target == document.querySelector('.img-modal-window .close img') || event.target == document.querySelector('.img-modal-window') || event.target == document.querySelector('.img-modal-window .modal-container')) {
      document.querySelector('.img-modal-window').classList.add('hide');
    }
  }); // show img full width

  function imgFullWidth(event) {
    var img = event.parentElement.parentElement.previousElementSibling;
    document.querySelector('.modal-img-wrapper .img-container').innerHTML = img.outerHTML;
    document.querySelector('.img-modal-window').classList.remove('hide');
  }
});
/******/ })()
;